var colors = require("colors");
var isDebugEnable = process.env.DEBUG



function error(title,message){
    log("error",title,message)
}

function success(title,message){
    log("success",title,message)
}
function warning(title,message){
    log("warning",title,message)
}
function rainbow(title,message){
    log("rainbow",title,message)
}

function log(type,title,message){
    if(!isDebugEnable)return false;
    
    switch (type){
        case "error":
            console.log(colors.red(title+": "+message));
            break;
        case "success":
            console.log(colors.green(title+": "+message));
            break;
        case "warning":
            console.log(colors.yellow(title+": "+message));
            break;
        case "rainbow":
            console.log(colors.rainbow(title+": "+message));
            break;
    }

}