module.exports = class Builder{

    constructor(){
        this.where = 0
        this.query = ""
    }

    where(key,op,val){
        if(this.where > 0){
            this.query = " ( "+key+" "+op+" "+(!isNaN(val)?parseInt(val):"'"+val+"'")+"";
        }else{
            this.query += " AND "+key+" "+op+" "+(!isNaN(val)?parseInt(val):"'"+val+"'")+"";
        }
        return this
    }
    
    whereIn(key,array){
        return this
    }

    whereNotIn(key,array){
        return this
    }



}

